function FileUpload(url, file){
	this.file = file;
	this.url = url;
}

FileUpload.prototype.getType = function() {
    return this.file.type;
};

FileUpload.prototype.getSize = function() {
    return this.file.size;
};

FileUpload.prototype.getName = function() {
    return this.file.name;
};

FileUpload.prototype.delete = function(navigator, id){
	nav.call(navigator, {intValue: id}, function(resp){
		$('.cloned[fileid="'+ id +'"]').remove();
		message.show('File deleted successfully.');
	});
};

FileUpload.prototype.doUpload = function (onsuccess, onerror, onprogress, params) {
    var that = this;
    var formData = new FormData();

    // add assoc key values, this will be posts values
    formData.append("file", this.file, this.getName());
    formData.append("upload_file", true);
    for (var key in params) {
    	formData.append(key, this.file, params[key]);
    }
    
    $.ajax({
        type: "POST",
        url: that.url,
        xhr: function () {
            var myXhr = $.ajaxSettings.xhr();
            if (myXhr.upload) {
                myXhr.upload.addEventListener('progress', function(event){
                	var progress = fileupload.progressHandling(event);
                	onprogress(progress);
                }, false);
            }
            return myXhr;
        },
        success: function (data) {
        	onsuccess(data);
        },
        error: function (error) {
        	onerror(error);
        },
        async: true,
        data: formData,
        cache: false,
        contentType: false,
        processData: false,
        timeout: 60000
    });
};

FileUpload.prototype.progressHandling = function (event) {
    var percent = 0;
    var position = event.loaded || event.position;
    var total = event.total;
    //var progress_bar_id = "#progress-wrp";
    if (event.lengthComputable) {
        percent = Math.ceil(position / total * 100);
    }
    // update progressbars classes so it fits your code
    //$(progress_bar_id + " .progress-bar").css("width", +percent + "%");
    //$(progress_bar_id + " .status").text(percent + "%");
    
    return percent;
};